function navegacionFija(){const e=document.querySelector(".header"),t=document.querySelector(".sobre-festival");window.addEventListener("scroll",(function(){t.getBoundingClientRect().bottom<1?e.classList.add("fixxed"):e.classList.remove("fixxed")}))}function crearGaleria(){const e=document.querySelector(".galeria-imagenes");for(let t=1;t<=16;t++){const n=document.createElement("PICTURE");n.innerHTML=`\n        <source srcset="dist/img/gallery/full/${t}.avif" type="image/avif">\n        <source srcset="dist/img/gallery/full/${t}.webp" type="image/webp">\n        <img loading="lazy" width="200" height="300" src="dist/img/gallery/full/${t}.jpg" alt="imagen galeria">\n    `,n.onclick=function(){mostrarImagen(t)},e.appendChild(n)}}function mostrarImagen(e){const t=document.createElement("PICTURE");t.innerHTML=`\n    <source srcset="dist/img/gallery/full/${e}.avif" type="image/avif">\n    <source srcset="dist/img/gallery/full/${e}.webp" type="image/webp">\n    <img loading="lazy" width="200" height="300" src="dist/img/gallery/full/${e}.jpg" alt="imagen galeria">\n`;const n=document.createElement("DIV");n.classList.add("modal"),n.onclick=cerrarModal,n.appendChild(t);const a=document.querySelector("body");a.classList.add("overflow-hidden"),a.appendChild(n)}function cerrarModal(){const e=document.querySelector(".modal");e.classList.add("fade-out"),setTimeout((()=>{document.querySelector("body").classList.remove("overflow-hidden"),e?.remove()}),300)}function resaltarEnlace(){document.addEventListener("scroll",(function(){const e=document.querySelectorAll("section"),t=document.querySelectorAll(".navegacion-principal a");let n="";e.forEach((e=>{const t=e.offsetTop,a=e.clientHeight;window.scrollY>=t-a/3&&(n=e.id)})),t.forEach((e=>{e.getAttribute("href")==="#"+n?e.classList.add("active"):e.classList.remove("active")}))}))}function scrollyt(){document.querySelectorAll(".navegacion-principal a").forEach((e=>{e.addEventListener("click",(e=>{e.preventDefault();const t=e.target.getAttribute("href");document.querySelector(t).scrollIntoView({behavior:"smooth"})}))}))}document.addEventListener("DOMContentLoaded",(function(){navegacionFija(),crearGaleria(),resaltarEnlace(),scrollyt()}));